function s_ = saveTrial(s_, varargin)% function s_= saveTrial(s_, varargin)%% overloaded saveTrial method for dXsaveToFIRA object% Copyright 2005 by Joshua I. Gold%   University of Pennsylvania% SAVE TRIALglobal ROOT_STRUCT% send command to update trialbuildFIRA_addTrial;% loop through saveToFIRA methodsfor sav = ROOT_STRUCT.methods.saveToFIRA    ROOT_STRUCT.(sav{:}) = saveToFIRA(ROOT_STRUCT.(sav{:}));end