function [t_, attributes_, batchMethot_] = dXtexture(num_objects)% function [t_, attributes_, batchMethot_] = dXtexture(num_objects)%% Constructor method for class dots% Input:%   num_objects     ... number of objects to create%% Output:%   t_              ... array of created texture instances%   attributes_     ... default object attributes%   batchMethot_    ... methods that can be run in a batch (e.g., draw)% Copyright 2007 by Benjamin Heasly%   University of Pennsylvania% get screen values[wn, sr, ppd, fr] = rGraphicsGetScreenAttributes;% default object attributesattributes = { ...    % name              type		values	default    'color',            'array',    [],		[1,1,1]*255; ... % RGBA or gray    'bgColor',          'array',    [],		[0,0,0]; ...    'lum',              'scalar',   [],     1;      ...    'bgLum',            'scalar',   [],     0;      ...    'contrast',         'scalar',   [],     1;,     ...    'privateBackground','boolean',  [],     false;	...    'textureFunction',  'string',   [],     'modelFestTexture'; ... %string or @    'textureArgs',      'scalar',   [],     {26};   ...    'duration',         'scalar',   [],     0;      ... % ms    'continuous',       'boolean',  [],     false;  ...    'x',                'scalar',	[],		0;		... % deg vis angle    'y',                'scalar',	[],		0;		... % deg vis angle    'w',                'scalar',	[],		10.0;   ... % deg vis angle    'h',                'scalar',	[],		10.0;   ... % deg vis angle    'sourceRect',       'array',    [],     [];     ... % [x,y,w,h] pix    'rotation',         'scalar',   [],     0;      ... % deg rotation    'filterMode',       'scalar',   [],     1;      ... % bilinear filter    'globalAlpha',      'scalar',   [],     1;      ... % blending opacity    'modulateColor',    'array',    [],     [];     ... % drawtime color change    'preload',          'boolean',  [],     true;   ...    'visible',          'boolean',	[],		0;		...    'tag',              'scalar',	[],		0;      ... % ignored    'isPreloaded',      'auto',     [],     false;  ...    'windowNumber',     'auto',     [],     wn;     ...    'screenRect',       'auto',     [],     sr;     ...    'pixelsPerDegree',  'auto',     [],     ppd;    ...    'frameRate',        'auto',     [],     fr;     ...    'drawRect',         'auto',     [],     [];     ... % where drawn    'images',           'auto',     [],     [];     ... % WxHxNX3    'imageIndices',     'auto',     [],     [];     ... % 1:N, N:1, etc.    'imageIndex',       'auto',     [],     0;      ...    'textures',         'auto',     [],     []};% make an array of objects from structs made from the attributesd = cell2struct(attributes(:,4), attributes(:,1), 1);for i = 1:num_objects    t_(i) = class(d, 'dXtexture');end% return the attributes, if necessaryif nargout > 1    attributes_ = attributes;end% return list of batch methodsif nargout > 2    batchMethot_ = {'draw', 'blank'};end