function [ts_, attributes_, batchMethods_] = dXvideo(num_objects)% function [ts_, attributes_, batchMethods_] = dXvideo(num_objects)%% Constructor method for class dXvideo%% Arguments:%   num_objects   ... number of objects to create%% Output:%   ts_           ... array of created dXvideo%   attributes_   ... default object attributes%   batchMethods_ ... methods that can be run in a batch (e.g., draw)% Copyright 2008 by Benjamin Heasly%   University of Pennsylvania% get screen attributes[wn, sr, ppd, fr] = rGraphicsGetScreenAttributes;% get path to Videos directory[defDir,n,e,v] = fileparts(mfilename('fullpath'));fis            = strfind(defDir, filesep);defDir         = [defDir(1:fis(end)) 'Videos' filesep];% default object attributesattributes = { ...        % name			type		ranges(?)	default        'visible',          'boolean',	[],		true;	...        'mute',             'boolean',  [],     false;  ...        'windowNumber',     'scalar',   [],     wn;     ...        'file',             'string',   [],     [];     ... % [dir/]file_name        'dir',              'string',   [],     defDir; ...        'asyncLoad',        'boolean',  [],     0;      ... % background loading        'rate',             'scalar',   [],     1;      ... % -1, 0, 1        'loop',             'boolean',  [],     false;  ...        'soundVolume',      'scalar',   [.01,1],1;      ...        'moviePtr',         'auto',     [],     nan;    ...        'movieDuration',    'auto',     [],     0;      ...        'movieFps',         'auto',     [],     0;      ...        'movieWidth',       'auto',     [],     0;      ...        'movieHeight',      'auto',     [],     0;      ...        'movieFrameCount',  'auto',     [],     0;      ...        'currentTexture',   'auto',     [],     nan;    ...        'droppedFrames',    'auto',     [],     0;      ...        'screenRect',       'auto',     [],     sr;     ...        'frameRate',        'auto',     [],     fr;     ...        'sr',               'auto',     [],     sr(3:4)./2; ...        'pixelsPerDegree',  'auto',     [],     ppd;    ...        'tag',              'auto',	    [],		0};           % ignored% make an array of objects from structs made from the attributest = cell2struct(attributes(:,4), attributes(:,1), 1);for i = 1:num_objects    ts_(i) = class(t, 'dXvideo');end% return the attributes, if necessaryif nargout > 1    attributes_ = attributes;endif nargout > 2    batchMethods_ = {'draw', 'blank', 'root'};end