function rGraphicsBlank% function rGraphicsBlank%% Turns everything off, restoring a blank screen.%   - Calls class-specific 'blank' methods, which in %       most cases just sets 'visible' flag to false%       (except dXscreen, which draws a background-color%           rectangle over the whole screen).%   - Flips the buffers once% Copyright 2004 by Joshua I. Gold%   University of Pennsylvaniaglobal ROOT_STRUCTswitch ROOT_STRUCT.screenMode        case 0                % debugMode        % loop through the drawables        for dr = ROOT_STRUCT.methods.blank            % call class-specific blank on ALL objects            ROOT_STRUCT.(dr{:}) = blank(ROOT_STRUCT.(dr{:}));        end    case 1                % Screen mode        % loop through the drawables        for dr = ROOT_STRUCT.methods.blank            % call class-specific blank on ALL objects            ROOT_STRUCT.(dr{:}) = blank(ROOT_STRUCT.(dr{:}));        end                % flip (& clear) buffers        Screen('Flip', ROOT_STRUCT.windowNumber);    case 2        % Remote mode        sendMsgH('rGraphicsBlankRex');end