function g_ = set(g_, varargin)%set method for class dXgameHID: specify property values and recompute dependencies%   g_ = set(g_, varargin)%%   All DotsX classes have set methods which allow properties for one or%   more instances to be specified, and dependent values recomputed.%%   Updated class instances are always returned.%%----------Special comments-----------------------------------------------%-%%-% assigns properties to an dXgameHID object%-% and returns the updated object%----------Special comments-----------------------------------------------%%   See also set dXgameHID% Copyright 2005 by Joshua I. Gold%   University of Pennsylvania% set the fields, one at a time...no error checkingfor ii = 1:2:nargin-1    g_.(varargin{ii}) = varargin{ii+1};end% activate after setting properties like HIDxCrieteria and HIDxChannelizerif any(strcmp(varargin, 'active'))    if g_.active        [g_.HIDIndex, g_.HIDDeviceInfo, g_.HIDElementsInfo] = ...            HIDx('add', g_.HIDClass, 1, g_.HIDCriteria, g_.HIDChannelizer);    else        g_.HIDIndex = HIDx('remove', g_.HIDIndex);    end    g_.active = ~isnan(g_.HIDIndex);end% if new mappings come in through set, work them outif ~isempty(g_.mappings)    g_ = putMap(g_, g_.mappings);    g_.mappings = [];end